import { Component } from "react";
import { Container, Row, Col, Alert, ListGroup } from "react-bootstrap";
import Image from "react-bootstrap/Image";

class GalleryOne extends Component {
  state = {
    films: [],
  };
  fetchFilms = () => {
    fetch("http://www.omdbapi.com/?apikey=266a7ae8 &s=Star Wars")
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((films) => {
        this.setState({ films: films.Search });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  componentDidMount() {
    this.fetchFilms();
  }

  render() {
    return (
      <Container className="bg-dark">
        <Row>
          {this.state.films.length > 0 && !this.state.isError && (
            <div className="d-flex flex-direction-row">
              {this.state.films.slice(0, 6).map((film) => {
                return (
                  <Col>
                    <Image src={film.Poster} className="img-fluid" />
                  </Col>
                );
              })}
            </div>
          )}
        </Row>
      </Container>
    );
  }
}
export default GalleryOne;
