import { Component } from "react";
import { Container, Row, Col, Alert, ListGroup } from "react-bootstrap";

class GalleryOne extends Component {
  state = {
    films: [],
  };
  fetchFilms = () => {
    fetch("http://www.omdbapi.com/?apikey=266a7ae8 &s=Star Wars")
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((films) => {
        console.log(films);

        this.setState({ films });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  componentDidMount() {
    this.fetchFilms();
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-center"></Row>
      </Container>
    );
  }
}
export default GalleryOne;
