import { Component } from "react";
import { Container, Row, Col, Alert, ListGroup } from "react-bootstrap";

class GalleryOne extends Component {
  state = {
    films: [],
  };
  fetchFilms = () => {
    fetch("http://www.omdbapi.com/?apikey=266a7ae8 &s=Star Wars")
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((films) => {
        this.setState({ films: films.Search });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  componentDidMount() {
    this.fetchFilms();
  }

  render() {
    return (
      <Container>
        <Row className="justify-content-center">
          <Col lg={2}>
            {this.state.films.length > 0 && !this.state.isError && (
              <ListGroup>
                {this.state.films.map((film) => {
                  return (
                    <ListGroup.Item key={film.imdbID} className="d-flex gap-2">
                      <span>{film.Title}</span>
                    </ListGroup.Item>
                  );
                })}

                <Card style={{ width: "18rem" }}>
                  <Card.Img variant="top" src="holder.js/100px180" />
                  <Card.Body>
                    <Card.Title>Card Title</Card.Title>
                    <Card.Text>
                      Some quick example text to build on the card title and make up the bulk of the card's content.
                    </Card.Text>
                    <Button variant="primary">Go somewhere</Button>
                  </Card.Body>
                </Card>
              </ListGroup>
            )}
          </Col>
        </Row>
      </Container>
    );
  }
}
export default GalleryOne;
