import { Component } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Image from "react-bootstrap/Image";

class GalleryThree extends Component {
  state = {
    films: [],
  };
  fetchFilms = () => {
    fetch("http://www.omdbapi.com/?apikey=266a7ae8&s=Avengers")
      .then((response) => {
        if (response.ok) {
          console.log("fetch conclusa");
          return response.json();
        } else {
          throw new Error("Errore nella richiesta delle prenotazioni al server");
        }
      })
      .then((films) => {
        this.setState({ films: films.Search });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  componentDidMount() {
    this.fetchFilms();
  }

  render() {
    return (
      <div className="bg-dark">
        <Container fluid>
          <Row>
            {this.state.films.length > 0 && !this.state.isError && (
              <div className="d-flex flex-direction-row">
                {this.state.films.slice(0, 6).map((film) => {
                  return (
                    <Col xs={2} key={film.imdbID}>
                      <Image src={film.Poster} className="img-fluid imageFilmPoster" />
                    </Col>
                  );
                })}
              </div>
            )}
          </Row>
        </Container>
      </div>
    );
  }
}
export default GalleryThree;
